const techData = {
  "Pantheon Platform": ["Dev/Test/Live workflows", "Multidev environments"],
  Core: ["Drupal Core", "WordPress Core"],
  "Languages & Databases": ["PHP", "MySQL", "Git"],
  "Advanced Caching & CDN": ["Varnish", "Fastly"],
  "Security Tools": ["HTTPS", "WAF", "Two-Factor Auth"],
  "CI/CD": ["GitHub Actions", "CircleCI integrated with Pantheon workflows"],
};

const techLogos = {
  "Dev/Test/Live workflows":
    "data:image/png;base64,",
  "Multidev environments":
    "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSqQYr5n8iIBly_Kdg-y2pfscuDzf4WpxBkYw&s",
  "Drupal Core":
    "data:image/png;base64,",
  "WordPress Core":
    "data:image/jpeg;base64,",
  PHP: "data:image/png;base64,",
  MySQL:
    "data:image/png;base64,",
  Git: "https://www.vectorlogo.zone/logos/redis/redis-icon.svg",
  Varnish:
    "data:image/png;base64,",
  Fastly:
    "https://upload.wikimedia.org/wikipedia/commons/thumb/8/8a/Fastly_logo.svg/1280px-Fastly_logo.svg.png",

  HTTPS:
    "data:image/png;base64,",
  WAF: "data:image/png;base64,",
  "Two-Factor Auth":
    "data:image/jpeg;base64,",
  "GitHub Actions":
    "data:image/png;base64,",
  "CircleCI integrated with Pantheon workflows":
    "https://www.drupal.org/files/Pantheon_logo_black_0.png",
};

const PantheonTechnologiesSection = () => {
  const categories = Object.keys(techData);
  const [selected, setSelected] = useState(categories[0]);

  return (
    <section className={styles.section}>
      <div className={styles.container}>
        <h2 className={styles.title}>Technologies We Use</h2>

        {/* Tabs */}
        <div className={styles.tabWrapper}>
          {categories.map((cat) => (
            <button
              key={cat}
              className={`${styles.tab} ${
                selected === cat ? styles.activeTab : ""
              }`}
              onClick={() => setSelected(cat)}
            >
              {cat}
            </button>
          ))}
        </div>

        {/* Tech Cards */}
        <div className={styles.cardGrid}>
          {techData[selected].map((item) => (
            <div key={item} className={styles.techCard}>
              <img
                src={techLogos[item]}
                alt={`${item} logo`}
                className={styles.logo}
              />
              <span>{item}</span>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default PantheonTechnologiesSection;
