import React, { useState } from "react";
import styles from "../../../../common-ui/design-and-development/development-technologies-dnd.module.css";

const CSharpTechnologies = () => {
  const techData = {
    "Frameworks": [".NET Framework", ".NET Core", "Blazor"],
    "Desktop Development": ["WPF", "UWP", "MAUI", "Xamarin"],
    "Data Access": ["Entity Framework", "Dapper", "LINQ", "NHibernate"],
    "Testing": ["NUnit", "SpecFlow", "FluentAssertions"]
  };

  const techLogos = {
    ".NET Framework": "https://upload.wikimedia.org/wikipedia/commons/7/7d/Microsoft_.NET_logo.svg",
    ".NET Core": "https://upload.wikimedia.org/wikipedia/commons/e/ee/.NET_Core_Logo.svg",
    "Blazor": "https://cdn.jsdelivr.net/gh/devicons/devicon@latest/icons/blazor/blazor-original.svg",
    "WPF": "data:image/png;base64,",
    "UWP": "https://cdn.iconscout.com/icon/premium/png-512-thumb/uwp-5363138-4488927.png?f=webp&w=512",
    "MAUI": "data:image/jpeg;base64,",
    "Xamarin": "https://cdn.jsdelivr.net/gh/devicons/devicon@latest/icons/xamarin/xamarin-original.svg",
    "Entity Framework": "https://cdn.jsdelivr.net/gh/devicons/devicon@latest/icons/entityframeworkcore/entityframeworkcore-original.svg",
    "Dapper": "https://avatars.githubusercontent.com/u/5117243?s=200&v=4",
    "LINQ": "data:image/png;base64,",
    "NHibernate": "https://cdn.jsdelivr.net/gh/devicons/devicon@latest/icons/nhibernate/nhibernate-original.svg",
    "NUnit": "data:image/png;base64,",
    "SpecFlow": "data:image/png;base64,",
    "FluentAssertions": "data:image/png;base64,"
  };

  const categories = Object.keys(techData);
  const [selected, setSelected] = useState(categories[0]);

  return (
    <section className={styles.section}>
      <div className={styles.container}>
        <h2 className={styles.title}>C# Technologies</h2>
        <div className={styles.tabWrapper}>
          {categories.map((cat) => (
            <button
              key={cat}
              className={`${styles.tab} ${selected === cat ? styles.activeTab : ""}`}
              onClick={() => setSelected(cat)}
            >
              {cat}
            </button>
          ))}
        </div>
        <div className={styles.cardGrid}>
          {techData[selected].map((item) => (
            <div key={item} className={styles.techCard}>
              <img
                src={techLogos[item]}
                alt={`${item} logo`}
                className={styles.logo}
                onError={(e) => { e.target.src = "https://via.placeholder.com/50"; }}
              />
              <span>{item}</span>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default CSharpTechnologies;
